request.fn:
function(opts) {
var request = require('hyperquest')
return request(opts.url)
}

through.fn:
function() {
var through = require('through2')
return through.obj(function (chunk, enc, cb){	this.push(chunk)
cb()
})
}

csv_parse.fn:
